mapper.select:
SELECT order_id, item_id, quantity, amount, due_date
FROM OrderDetail
WHERE order_id = #{orderId}

SELECT order_id, item_id, quantity, amount, due_date
FROM OrderDetail
WHERE item_id = #{itemId}

mapper.update:
UPDATE OrderDetail
SET item_id = #{item_Id}, quantity = #{quantity}, amount = #{amount}, due_date = #{due_date}
WHERE order_id = #{orderId}
