mapper.select:
SELECT order_id, user_id, type, item_id, item_location_id, quantity, order_date, amount, due_date, return_date, order_status
FROM Orders
WHERE order_id = #{orderId}

SELECT order_id, user_id, type, item_id, item_location_id, quantity, order_date, amount, due_date, return_date, order_status
FROM Orders
WHERE user_id = #{userId}

SELECT order_id, user_id, type, item_id, item_location_id, quantity, order_date, amount, due_date, return_date, order_status
FROM Orders
WHERE item_id = #{itemId}

mapper.update:
UPDATE Orders
SET user_id = #{userId}, type = #{type}, item_id = #{itemId}, item_location_id = #{itemLocationId}, quantity = #{quantity}, order_date = #{orderDate}, amount = #{amount}, due_date = #{dueDate}, return_date = #{returnDate}, order_status = #{orderStatus}
WHERE order_id = #{orderId}
